import React, { Suspense, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, Environment, Html } from '@react-three/drei';

// Loading component
function LoadingSpinner() {
  return (
    <Html center>
      <div style={{
        color: '#ffffff',
        fontSize: '18px',
        textAlign: 'center'
      }}>
        <div style={{
          border: '4px solid #f3f3f3',
          borderTop: '4px solid #3498db',
          borderRadius: '50%',
          width: '40px',
          height: '40px',
          animation: 'spin 1s linear infinite',
          margin: '0 auto 10px'
        }}></div>
        Loading 3D Model...
      </div>
    </Html>
  );
}

// 3D Model Component
function Model({ url }) {
  const { scene } = useGLTF(url);

  // Auto-fit model to scene
  React.useEffect(() => {
    if (scene) {
      // Center and scale model
      const box = new THREE.Box3().setFromObject(scene);
      const center = box.getCenter(new THREE.Vector3());
      const size = box.getSize(new THREE.Vector3());

      // Center the model
      scene.position.sub(center);

      // Scale to fit (adjust multiplier as needed)
      const maxDim = Math.max(size.x, size.y, size.z);
      const scale = 5 / maxDim;
      scene.scale.setScalar(scale);
    }
  }, [scene]);

  return <primitive object={scene} />;
}

export default function App() {
  const [modelUrl, setModelUrl] = useState('/assets/model.glb');
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setModelUrl(url);
      setError(null);
    }
  };

  return (
    <div style={{
      width: '100vw',
      height: '100vh',
      margin: 0,
      padding: 0,
      fontFamily: 'Arial, sans-serif',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      {/* Header */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        zIndex: 100,
        padding: '20px',
        background: 'rgba(0,0,0,0.1)',
        backdropFilter: 'blur(10px)'
      }}>
        <h1 style={{
          color: 'white',
          margin: '0 0 10px 0',
          fontSize: '24px'
        }}>
          3D Model Viewer
        </h1>

        {/* File input */}
        <input
          type="file"
          accept=".glb,.gltf"
          onChange={handleFileChange}
          style={{
            background: 'rgba(255,255,255,0.2)',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '4px',
            color: 'white'
          }}
        />
      </div>

      {/* 3D Canvas */}
      <Canvas
        camera={{ position: [0, 0, 10], fov: 50 }}
        style={{ width: '100%', height: '100%' }}
        onError={() => setError('Failed to load 3D model')}
      >
        {/* Lighting */}
        <ambientLight intensity={0.6} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <pointLight position={[-10, -10, -5]} intensity={0.5} />

        {/* Environment for reflections */}
        <Environment preset="studio" />

        {/* Model with loading fallback */}
        <Suspense fallback={<LoadingSpinner />}>
          {modelUrl && <Model url={modelUrl} />}
        </Suspense>

        {/* Camera controls */}
        <OrbitControls
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          autoRotate={false}
          autoRotateSpeed={0.5}
        />
      </Canvas>

      {/* Error message */}
      {error && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px',
          background: 'rgba(255,0,0,0.8)',
          color: 'white',
          padding: '15px',
          borderRadius: '4px',
          textAlign: 'center'
        }}>
          {error}
        </div>
      )}

      {/* Controls info */}
      <div style={{
        position: 'absolute',
        bottom: '20px',
        right: '20px',
        background: 'rgba(0,0,0,0.7)',
        color: 'white',
        padding: '10px',
        borderRadius: '4px',
        fontSize: '12px'
      }}>
        <div>🖱️ Click + Drag: Rotate</div>
        <div>🔍 Scroll: Zoom</div>
        <div>⌨️ Right Click + Drag: Pan</div>
      </div>

      {/* CSS Animation */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        body {
          margin: 0;
          overflow: hidden;
        }
      `}</style>
    </div>
  );
}
